import { useState } from "react";
function Login() {
  function loginChange(e) {
    const { name, value } = e.target;
    changeLogin((prev) => {
      return {
        ...prev,
        [name]: value,
      };
    });
  }
  function submission(e) {
    e.preventDefault();
    fetch("http://localhost:3000/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      // headers: { credentials: "include", withCredentials: true },
      body: JSON.stringify(login),
    }).then((res) => {
      console.log(res.status); // Will show you the status
      if (!response.ok) {
        throw new Error("HTTP status " + response.status);
      }
      return response
        .json()
        .then((data) => console.log(data, status))
        .catch((err) => console.log(err));
      changeLogin({ email: "", password: "" });
    });
  }

  const [login, changeLogin] = useState({ email: "", password: "" });
  return (
    <form>
      <input
        type="email"
        name="email"
        placeholder="Email"
        onChange={loginChange}
        value={login.email}
      />
      <input
        type="password"
        name="password"
        placeholder="Password"
        onChange={loginChange}
        value={login.password}
      />
      <button onClick={submission} type="submit">
        Submit
      </button>
    </form>
  );
}
export default Login;
